/*
  if you are doing all your html dynamically...
  this is all you need.
  add options to send the style sheets?
  also, for apps that you need to get indexed, you gotta have a static page.
  maybe investigate template engines that do that?
*/
module.exports = function () {
  return function (req, res, next) {
    if(req.url == '/')
      res.end('<html><script src=/browserify.js></script></html>')
    else next()
  }
}
